import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { coronaAPI } from 'features/corona';
const TODAY = async () => {
   const res = await coronaAPI.today()
   return res.data
}
const WEEK = async () => {
    const res = await coronaAPI.week()
    return res.data
 }
const CONFIRMED = async () => {
    const res = await coronaAPI.confirmed()
    return res.data
 }
export const getToday = createAsyncThunk('/today', TODAY)
export const getWeek = createAsyncThunk('/week', WEEK)
export const getConfirmed = createAsyncThunk('/confirmed', CONFIRMED)
const coronaSlice = createSlice({
  name: 'chart',
  initialState: {
    todayState: {
      death: "",
      serious: "",
      new_hospitalization: "",
      confirmed: "",
      update_at: ""
    },
    weekState: {
      death: "",
      serious: "",
      new_hospitalization: "",
      confirmed: "",
      update_at: ""
    },
    confirmedState: [],
    type: '',
    keyword: '',
    params: {}
  },
  reducers: {},
  extraReducers: {
    [getToday.fulfilled]: ( state, action ) => {
      state.todayState = action.payload
    },
    [getWeek.fulfilled]: ( state, action ) => {
      state.weekState = action.payload
    },
    [getConfirmed.fulfilled]: ( state, action ) => {
      // action.payload.forEach(element => {
        // state.confirmedState = [...state.confirmedState, {lineValue: element.confirmed, argument: element.date}]
        // });
        [...Array(60).keys()].map(i => state.confirmedState = [...state.confirmedState, {lineValue: action.payload[i].confirmed, argument: action.payload[i].date}]);
        // state.confirmedState = [...state.confirmedState, {lineValue: element.confirmed, argument: element.date}]
    },
  }
})
// export const currentMapState = state => state.chart.mapState
// export const currentMapsState = state => state.chart.mapsState
export default coronaSlice.reducer;
