import * as React from 'react';
import Paper from '@material-ui/core/Paper';
import {
  Chart,
  BarSeries,
  ArgumentAxis,
  ValueAxis,
} from '@devexpress/dx-react-chart-material-ui';

import { EventTracker, SelectionState } from '@devexpress/dx-react-chart';
import { useState } from 'react';

const data = [
  { label: '1950', population: 2.525 },
  { label: '1960', population: 3.018 },
  { label: '1970', population: 3.682 },
  { label: '1980', population: 4.440 },
  { label: '1990', population: 5.310 },
  { label: '2000', population: 6.127 },
  { label: '2010', population: 6.930 },
];

const compare = (
  { series, point }, { series: targetSeries, point: targetPoint },
) => series === targetSeries && point === targetPoint;

export default function StackChart (){
    const [ selection, setSelection] = useState([])
    const btn = ({ targets }) => {
      const target = targets[0];
      if (target) {
        this.setState(({ selection }) => ({
          selection: selection[0] && compare(selection[0], target) ? [] : [target],
        }));
      }
    };

    return (
      <div>
        <span>
          Selected value:
          {' '}
          {selection.length ? data[selection[0].point].population : undefined}
        </span>
        <Paper>
          <Chart
            data={data}
          >
            <ArgumentAxis />
            <ValueAxis />

            <BarSeries
              valueField="population"
              argumentField="label"
            />
            <EventTracker onClick={this.click} />
            <SelectionState selection={selection} />
          </Chart>
        </Paper>
      </div>
    );
  }

