import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { coronaAPI } from 'features/corona';
const TODAY = async () => {
   const res = await coronaAPI.today()
   return res.data
}
const WEEK = async () => {
    const res = await coronaAPI.week()
    return res.data
 }
const CONFIRMED = async () => {
    const res = await coronaAPI.confirmed()
    return res.data
 }
export const getToday = createAsyncThunk('/today', TODAY)
export const getWeek = createAsyncThunk('/week', WEEK)
export const getConfirmed = createAsyncThunk('/confirmed', CONFIRMED)
const coronaSlice = createSlice({
  name: 'chart',
  initialState: {
    mapState: {
      name:''
    },
    mapsState: [],
    type: '',
    keyword: '',
    params: {}
  },
  reducers: {},
  extraReducers: {
    [worldMap.fulfilled]: ( state, action ) => {
      state.mapsState = action.payload
    },
    [medPoint.fulfilled]: ( state, action ) => {
      state.mapState = action.payload
    },
    [medPoints.fulfilled]: ( state, action ) => {
      state.mapsState = action.payload
    },
    [casesPoints.fulfilled]: ( state, action ) => {
      state.mapsState = action.payload
      state.mapState = {
        name:''
      }
    },
  }
})
// export const currentMapState = state => state.chart.mapState
// export const currentMapsState = state => state.chart.mapsState
export default coronaSlice.reducer;
